mapper.select:
SELECT a.class_no ,a.member_id ,a.status ,a.idx ,l.lightning_no
FROM apply a
JOIN lightningList l ON a.member_id = l.leader_id WHERE a.status = "대기" AND a.class_no = 1 AND l.lightning_no = #{param1}

mapper.update:
UPDATE apply a
JOIN codeTable ON a.class_no ON c.class_no = 1
LEFT OUTER JOIN lightningList l ON l.class_no = c.class_no
SET a.status = #{lightning_status}
WHERE a.idx = #{lightning_no} AND a.member_id = #{member_id}

UPDATE apply a
JOIN codeTable ON a.class_no ON c.class_no = 3
LEFT OUTER JOIN dojangList d ON d.class_no = c.class_no
SET a.status = #{dojang_status}
WHERE a.idx = #{dojang_no} AND a.member_id = #{member_id}

mapper.select:
SELECT g.groupReview_no,g.review_title,g.review_date
FROM groupReview g
LEFT OUTER JOIN lightningList l on l.lightning_no = idx and g.class_no = 1
LEFT OUTER JOIN dojangList d ON d.dojang_no = idx AND g.class_no = 3

	g.groupReview_status = "노출"

		l.lightning_no = #{idx}


		d.dojang_no = #{idx}


ORDER BY groupReview_no DESC limit #{cnt} OFFSET #{offset}

SELECT COUNT(groupReview_no)
FROM groupReview g
LEFT OUTER JOIN lightningList l ON l.lightning_no = idx AND g.class_no = 1
LEFT OUTER JOIN dojangList d ON d.dojang_no = idx AND g.class_no = 3

	g.groupReview_status = "노출"

		l.lightning_no = #{idx}


		d.dojang_no = #{idx}


ORDER BY groupReview_no DESC limit #{cnt} OFFSET #{offset}

SELECT l.lightning_title ,d.dojang_title ,l.class_no ,d.class_no ,m.member_id ,(SELECT a.member_id FROM apply a WHERE a.status = "승인" AND l.lightning_no = #{param3}) AS lightning_member ,(SELECT a.member_id FROM apply a WHERE a.status = "승인" AND d.dojang_no = #{param3}) AS dojang_member ,(SELECT COUNT(groupReview_no) FROM groupReview g WHERE g.member_id =#{param1}) AS post_count ,(SELECT COUNT(comment_no) FROM comment c WHERE c.member_id =#{param1}) AS comment_count ,(SELECT photo_newFileName FROM photo p WHERE p.class_no=9 AND p.idx = (SELECT profile_no FROM profile f WHERE f.member_id =#{param1})) AS photo_newFileName FROM groupReview g LEFT OUTER JOIN lightningList l ON l.lightning_no = idx and groupReview.class_no = 1 LEFT OUTER JOIN dojangList d ON d.dojang_no = idx AND groupReview.class_no = 3 LEFT OUTER JOIN member ON member.member_id = groupReview.member_id JOIN codeTable ON groupReview.class_no = codeTable.class_no
l.lightning_no = #{param3}
d.dojang_no = #{param3}

SELECT (SELECT a.member_id FROM apply a WHERE a.status = "승인" AND l.lightning_no = idx) AS lightning_member
	,(SELECT a.member_id FROM apply a WHERE a.status = "승인" AND d.dojang_no = idx) AS dojang_member
	,m.member_name
FROM member m


		l.lightning_no = #{idx}


		d.dojang_no = #{idx}

SELECT (SELECT a.member_id FROM apply a WHERE a.status = "승인" AND l.lightning_no = idx) AS lightning_member
	,(SELECT a.member_id FROM apply a WHERE a.status = "승인" AND d.dojang_no = idx) AS dojang_member
	,m.member_name
FROM member m


		l.lightning_no = #{idx}


		d.dojang_no = #{idx}
